import { Component } from '@angular/core';

@Component({
  selector: 'app-camp-tips',
  templateUrl: './camp-tips.component.html',
  styleUrls: ['./camp-tips.component.css']
})
export class CampTipsComponent {

}
<h2 class="title-main">Camp Tips</h2>

<div>
    <ul>

        <li class="tip">Apply early- </li>
        <p class="more-info">Most camps have limited capacity and could be full if you're applying last minute.</p>

    </ul>
</div>
